import React from 'react';
import { EmailComponent, ComponentType } from '../../types';
import { ColorPicker } from './ColorPicker';

interface ComponentInspectorProps {
  selectedComponent: EmailComponent;
  updateComponent: (id: string, updates: Partial<EmailComponent>) => void;
  deleteComponent: (id: string) => void;
  duplicateComponent: (id: string) => void;
}

export const ComponentInspector: React.FC<ComponentInspectorProps> = ({
  selectedComponent,
  updateComponent,
  deleteComponent,
  duplicateComponent
}) => {
  const [showDeleteConfirm, setShowDeleteConfirm] = React.useState(false);
  const [footerPanels, setFooterPanels] = React.useState({
    header: true,
    address: true,
    social: true,
    unsubscribe: true
  });

  const handlePropertyChange = (key: string, value: any) => {
    updateComponent(selectedComponent.id, {
      props: {
        ...selectedComponent.props,
        [key]: value,
      },
    });
  };

  const handleDelete = () => {
    setShowDeleteConfirm(true);
  };

  const confirmDelete = () => {
    deleteComponent(selectedComponent.id);
    setShowDeleteConfirm(false);
  };

  const cancelDelete = () => {
    setShowDeleteConfirm(false);
  };

  const handleDuplicate = () => {
    duplicateComponent(selectedComponent.id);
  };

  const renderPropertyEditor = () => {
    switch (selectedComponent.type) {
      case 'header':
        return (
          <div className="space-y-4">
            <div>
              <label className="block text-sm font-medium text-gray-700 mb-1">
                Logo URL
              </label>
              <input
                type="text"
                value={selectedComponent.props.logo || ''}
                onChange={(e) => handlePropertyChange('logo', e.target.value)}
                className="input-field"
                placeholder="https://example.com/logo.png"
              />
            </div>
            <div>
              <label className="block text-sm font-medium text-gray-700 mb-1">
                Logo Width
              </label>
              <input
                type="text"
                value={selectedComponent.props.logoWidth || '200px'}
                onChange={(e) => handlePropertyChange('logoWidth', e.target.value)}
                className="input-field"
                placeholder="200px, 100%, auto"
              />
            </div>
            <div>
              <label className="block text-sm font-medium text-gray-700 mb-1">
                Logo Height
              </label>
              <input
                type="text"
                value={selectedComponent.props.logoHeight || '60px'}
                onChange={(e) => handlePropertyChange('logoHeight', e.target.value)}
                className="input-field"
                placeholder="60px, 80px, auto"
              />
            </div>
            <div>
              <label className="block text-sm font-medium text-gray-700 mb-1">
                Title
              </label>
              <input
                type="text"
                value={selectedComponent.props.title || ''}
                onChange={(e) => handlePropertyChange('title', e.target.value)}
                className="input-field"
                placeholder="Company Name"
              />
            </div>
            <div>
              <label className="block text-sm font-medium text-gray-700 mb-1">
                Subtitle
              </label>
              <input
                type="text"
                value={selectedComponent.props.subtitle || ''}
                onChange={(e) => handlePropertyChange('subtitle', e.target.value)}
                className="input-field"
                placeholder="Tagline or description"
              />
            </div>
            <div>
              <ColorPicker
                value={selectedComponent.props.backgroundColor || '#ffffff'}
                onChange={(color) => handlePropertyChange('backgroundColor', color)}
                label="Background Color"
                placeholder="Select background color"
              />
            </div>
            <div>
              <ColorPicker
                value={selectedComponent.props.textColor || '#000000'}
                onChange={(color) => handlePropertyChange('textColor', color)}
                label="Text Color"
                placeholder="Select text color"
              />
            </div>
          </div>
        );

      case 'text':
        return (
          <div className="space-y-4">
            <div>
              <label className="block text-sm font-medium text-gray-700 mb-1">
                Content
              </label>
              <textarea
                value={selectedComponent.props.content || ''}
                onChange={(e) => handlePropertyChange('content', e.target.value)}
                className="input-field"
                rows={4}
                placeholder="Enter your text content here..."
              />
            </div>
            <div>
              <label className="block text-sm font-medium text-gray-700 mb-1">
                Font Size
              </label>
              <select
                value={selectedComponent.props.fontSize || '16px'}
                onChange={(e) => handlePropertyChange('fontSize', e.target.value)}
                className="input-field"
              >
                <option value="12px">12px</option>
                <option value="14px">14px</option>
                <option value="16px">16px</option>
                <option value="18px">18px</option>
                <option value="20px">20px</option>
                <option value="24px">24px</option>
              </select>
            </div>
            <div>
              <label className="block text-sm font-medium text-gray-700 mb-1">
                Text Align
              </label>
              <select
                value={selectedComponent.props.textAlign || 'left'}
                onChange={(e) => handlePropertyChange('textAlign', e.target.value)}
                className="input-field"
              >
                <option value="left">Left</option>
                <option value="center">Center</option>
                <option value="right">Right</option>
              </select>
            </div>
            <div>
              <ColorPicker
                value={selectedComponent.props.textColor || '#000000'}
                onChange={(color) => handlePropertyChange('color', color)}
                label="Text Color"
                placeholder="Select text color"
              />
            </div>
          </div>
        );

      case 'image':
        return (
          <div className="space-y-4">
            <div>
              <label className="block text-sm font-medium text-gray-700 mb-1">
                Image URL
              </label>
              <input
                type="text"
                value={selectedComponent.props.src || ''}
                onChange={(e) => handlePropertyChange('src', e.target.value)}
                className="input-field"
                placeholder="https://example.com/image.jpg"
              />
            </div>
            <div>
              <label className="block text-sm font-medium text-gray-700 mb-1">
                Alt Text
              </label>
              <input
                type="text"
                value={selectedComponent.props.alt || ''}
                onChange={(e) => handlePropertyChange('alt', e.target.value)}
                className="input-field"
                placeholder="Description of the image"
              />
            </div>
            <div>
              <label className="block text-sm font-medium text-gray-700 mb-1">
                Width
              </label>
              <input
                type="text"
                value={selectedComponent.props.width || '100%'}
                onChange={(e) => handlePropertyChange('width', e.target.value)}
                className="input-field"
                placeholder="100% or 300px"
              />
            </div>
            <div>
              <label className="block text-sm font-medium text-gray-700 mb-1">
                Alignment
              </label>
              <select
                value={selectedComponent.props.align || 'center'}
                onChange={(e) => handlePropertyChange('align', e.target.value)}
                className="input-field"
              >
                <option value="left">Left</option>
                <option value="center">Center</option>
                <option value="right">Right</option>
              </select>
            </div>
          </div>
        );

      case 'button':
        return (
          <div className="space-y-4">
            <div>
              <label className="block text-sm font-medium text-gray-700 mb-1">
                Button Text
              </label>
              <input
                type="text"
                value={selectedComponent.props.text || ''}
                onChange={(e) => handlePropertyChange('text', e.target.value)}
                className="input-field"
                placeholder="Click me"
              />
            </div>
            <div>
              <label className="block text-sm font-medium text-gray-700 mb-1">
                URL
              </label>
              <input
                type="text"
                value={selectedComponent.props.url || ''}
                onChange={(e) => handlePropertyChange('url', e.target.value)}
                className="input-field"
                placeholder="https://example.com"
              />
            </div>
            <div>
              <ColorPicker
                value={selectedComponent.props.backgroundColor || '#3b82f6'}
                onChange={(color) => handlePropertyChange('backgroundColor', color)}
                label="Background Color"
                placeholder="Select background color"
              />
            </div>
            <div>
              <ColorPicker
                value={selectedComponent.props.textColor || '#ffffff'}
                onChange={(color) => handlePropertyChange('textColor', color)}
                label="Text Color"
                placeholder="Select text color"
              />
            </div>
          </div>
        );

      case 'footer':
        return (
          <div className="space-y-4">
            {/* Header Panel */}
            <div className="border border-gray-200 rounded-lg">
              <button
                onClick={() => setFooterPanels(prev => ({ ...prev, header: !prev.header }))}
                className="flex items-center justify-between w-full p-3 text-left hover:bg-gray-50 transition-colors"
              >
                <h4 className="text-sm font-medium text-gray-700">Header</h4>
                <svg
                  className={`w-4 h-4 text-gray-500 transition-transform ${footerPanels.header ? 'rotate-180' : ''}`}
                  fill="none"
                  stroke="currentColor"
                  viewBox="0 0 24 24"
                >
                  <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M19 9l-7 7-7-7" />
                </svg>
              </button>

              {footerPanels.header && (
                <div className="p-3 border-t border-gray-200 space-y-3">
                  <div>
                    <label className="block text-sm font-medium text-gray-700 mb-1">
                      Title
                    </label>
                    <input
                      type="text"
                      value={selectedComponent.props.companyName || ''}
                      onChange={(e) => handlePropertyChange('companyName', e.target.value)}
                      className="input-field"
                      placeholder="Company Name"
                    />
                  </div>
                  <div>
                    <ColorPicker
                      value={selectedComponent.props.backgroundColor || 'transparent'}
                      onChange={(color) => handlePropertyChange('backgroundColor', color)}
                      label="Background Color"
                      placeholder="Select background color"
                    />
                  </div>
                </div>
              )}
            </div>

            {/* Address Panel */}
            <div className="border border-gray-200 rounded-lg">
              <button
                onClick={() => setFooterPanels(prev => ({ ...prev, address: !prev.address }))}
                className="flex items-center justify-between w-full p-3 text-left hover:bg-gray-50 transition-colors"
              >
                <h4 className="text-sm font-medium text-gray-700">Address</h4>
                <svg
                  className={`w-4 h-4 text-gray-500 transition-transform ${footerPanels.address ? 'rotate-180' : ''}`}
                  fill="none"
                  stroke="currentColor"
                  viewBox="0 0 24 24"
                >
                  <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M19 9l-7 7-7-7" />
                </svg>
              </button>

              {footerPanels.address && (
                <div className="p-3 border-t border-gray-200 space-y-3">
                  <div>
                    <label className="block text-sm font-medium text-gray-700 mb-1">
                      Address
                    </label>
                    <input
                      type="text"
                      value={selectedComponent.props.address || ''}
                      onChange={(e) => handlePropertyChange('address', e.target.value)}
                      className="input-field"
                      placeholder="123 Main St, City, State 12345"
                    />
                  </div>
                  <div>
                    <label className="block text-sm font-medium text-gray-700 mb-1">
                      Phone
                    </label>
                    <input
                      type="text"
                      value={selectedComponent.props.phone || ''}
                      onChange={(e) => handlePropertyChange('phone', e.target.value)}
                      className="input-field"
                      placeholder="+1 (555) 123-4567"
                    />
                  </div>
                  <div>
                    <label className="block text-sm font-medium text-gray-700 mb-1">
                      Email
                    </label>
                    <input
                      type="text"
                      value={selectedComponent.props.email || ''}
                      onChange={(e) => handlePropertyChange('email', e.target.value)}
                      className="input-field"
                      placeholder="info@company.com"
                    />
                  </div>
                </div>
              )}
            </div>

            {/* Social Links Panel */}
            <div className="border border-gray-200 rounded-lg">
              <button
                onClick={() => setFooterPanels(prev => ({ ...prev, social: !prev.social }))}
                className="flex items-center justify-between w-full p-3 text-left hover:bg-gray-50 transition-colors"
              >
                <h4 className="text-sm font-medium text-gray-700">Social Links</h4>
                <svg
                  className={`w-4 h-4 text-gray-500 transition-transform ${footerPanels.social ? 'rotate-180' : ''}`}
                  fill="none"
                  stroke="currentColor"
                  viewBox="0 0 24 24"
                >
                  <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M19 9l-7 7-7-7" />
                </svg>
              </button>

              {footerPanels.social && (
                <div className="p-3 border-t border-gray-200 space-y-3">
                  <div>
                    <label className="block text-sm font-medium text-gray-700 mb-1">
                      Facebook URL
                    </label>
                    <input
                      type="text"
                      value={selectedComponent.props.socialLinks?.[0]?.url || ''}
                      onChange={(e) => {
                        const newSocialLinks = [...(selectedComponent.props.socialLinks || [])];
                        if (newSocialLinks[0]) {
                          newSocialLinks[0] = { platform: 'Facebook', url: e.target.value };
                        } else {
                          newSocialLinks.push({ platform: 'Facebook', url: e.target.value });
                        }
                        handlePropertyChange('socialLinks', newSocialLinks);
                      }}
                      className="input-field"
                      placeholder="https://facebook.com/company"
                    />
                  </div>
                  <div>
                    <label className="block text-sm font-medium text-gray-700 mb-1">
                      Twitter URL
                    </label>
                    <input
                      type="text"
                      value={selectedComponent.props.socialLinks?.[1]?.url || ''}
                      onChange={(e) => {
                        const newSocialLinks = [...(selectedComponent.props.socialLinks || [])];
                        if (newSocialLinks[1]) {
                          newSocialLinks[1] = { platform: 'Twitter', url: e.target.value };
                        } else {
                          newSocialLinks.push({ platform: 'Twitter', url: e.target.value });
                        }
                        handlePropertyChange('socialLinks', newSocialLinks);
                      }}
                      className="input-field"
                      placeholder="https://twitter.com/company"
                    />
                  </div>
                  <div>
                    <label className="block text-sm font-medium text-gray-700 mb-1">
                      LinkedIn URL
                    </label>
                    <input
                      type="text"
                      value={selectedComponent.props.socialLinks?.[2]?.url || ''}
                      onChange={(e) => {
                        const newSocialLinks = [...(selectedComponent.props.socialLinks || [])];
                        if (newSocialLinks[1]) {
                          newSocialLinks[2] = { platform: 'LinkedIn', url: e.target.value };
                        } else {
                          newSocialLinks.push({ platform: 'LinkedIn', url: e.target.value });
                        }
                        handlePropertyChange('socialLinks', newSocialLinks);
                      }}
                      className="input-field"
                      placeholder="https://linkedin.com/company/company"
                    />
                  </div>
                </div>
              )}
            </div>

            {/* Unsubscribe Panel */}
            <div className="border border-gray-200 rounded-lg">
              <button
                onClick={() => setFooterPanels(prev => ({ ...prev, unsubscribe: !prev.unsubscribe }))}
                className="flex items-center justify-between w-full p-3 text-left hover:bg-gray-50 transition-colors"
              >
                <h4 className="text-sm font-medium text-gray-700">Unsubscribe</h4>
                <svg
                  className={`w-4 h-4 text-gray-500 transition-transform ${footerPanels.unsubscribe ? 'rotate-180' : ''}`}
                  fill="none"
                  stroke="currentColor"
                  viewBox="0 0 24 24"
                >
                  <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M19 9l-7 7-7-7" />
                </svg>
              </button>

              {footerPanels.unsubscribe && (
                <div className="p-3 border-t border-gray-200 space-y-3">
                  <div>
                    <label className="block text-sm font-medium text-gray-700 mb-1">
                      Unsubscribe Text
                    </label>
                    <input
                      type="text"
                      value={selectedComponent.props.unsubscribeText || ''}
                      onChange={(e) => handlePropertyChange('unsubscribeText', e.target.value)}
                      className="input-field"
                      placeholder="Click here to unsubscribe"
                    />
                  </div>
                  <div>
                    <label className="block text-sm font-medium text-gray-700 mb-1">
                      Unsubscribe URL
                    </label>
                    <input
                      type="text"
                      value={selectedComponent.props.unsubscribeUrl || ''}
                      onChange={(e) => handlePropertyChange('unsubscribeUrl', e.target.value)}
                      className="input-field"
                      placeholder="https://company.com/unsubscribe"
                    />
                  </div>
                </div>
              )}
            </div>
          </div>
        );

      default:
        return (
          <div className="text-gray-500 text-sm">
            Properties for this component type are not yet implemented.
          </div>
        );
    }
  };

  if (showDeleteConfirm) {
    return (
      <div className="text-center">
        <h3 className="text-lg font-medium text-gray-900 mb-4">
          Are you sure you want to delete{' '}
          <span className="text-primary-600 font-semibold capitalize">
            {selectedComponent.type}
          </span>{' '}
          Component?
        </h3>
        <div className="flex justify-center space-x-3">
          <button
            onClick={cancelDelete}
            className="px-4 py-2 bg-gray-200 text-gray-700 rounded-lg hover:bg-gray-300 transition-colors"
          >
            No
          </button>
          <button
            onClick={confirmDelete}
            className="px-4 py-2 bg-red-500 text-white rounded-lg hover:bg-red-600 transition-colors"
          >
            Yes
          </button>
        </div>
      </div>
    );
  }

  return (
    <>
      <div className="flex items-center justify-between mb-4">
        <div className="flex space-x-2">
          <button
            onClick={handleDuplicate}
            className="p-2 text-gray-600 hover:text-gray-800 hover:bg-gray-100 rounded-lg transition-colors"
            title="Duplicate"
          >
            <svg className="w-4 h-4" fill="none" stroke="currentColor" viewBox="0 0 24 24">
              <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M8 16H6a2 2 0 01-2-2V6a2 2 0 012-2h8a2 2 0 012 2v2m-6 12h8a2 2 0 002-2v-8a2 2 0 00-2-2h-8a2 2 0 00-2 2v8a2 2 0 002 2z" />
            </svg>
          </button>
          <button
            onClick={handleDelete}
            className="p-2 text-red-600 hover:text-red-800 hover:bg-red-50 rounded-lg transition-colors"
            title="Delete"
          >
            <svg className="w-4 h-4" fill="none" stroke="currentColor" viewBox="0 0 24 24">
              <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M19 7l-.867 12.142A2 2 0 0116.138 21H7.862a2 2 0 01-1.995-1.858L5 7m5 4v6m4-6v6m1-10V4a1 1 0 00-1-1h-4a1 1 0 00-1 1v3M4 7h16" />
            </svg>
          </button>
        </div>
      </div>

      <div className="mb-4">
        <div className="text-sm text-gray-500 mb-1">Component Type</div>
        <div className="text-sm font-medium text-gray-900 capitalize">
          {selectedComponent.type}
        </div>
      </div>

      <div className="border-t border-gray-200 pt-4">
        <h3 className="text-sm font-medium text-gray-700 mb-3">Properties</h3>
        {renderPropertyEditor()}
      </div>
    </>
  );
};
