import React, { useState } from 'react';
import { useDrop } from 'react-dnd';
import { useBuilderStore } from '../../store/builderStore';
import { EmailComponent, ComponentType } from '../../types';
import { DraggableComponent } from './DraggableComponent';
import { Preview } from './Preview';

export const Builder: React.FC = () => {
  const [isPreviewMode, setIsPreviewMode] = useState(false);
  const { 
    template, 
    addComponent, 
    selectComponent, 
    selectedComponent,
    moveComponent 
  } = useBuilderStore();

  const [{ isOver }, drop] = useDrop({
    accept: 'COMPONENT',
    drop: (item: { type: ComponentType }, monitor) => {
      if (!monitor.didDrop()) {
        const newComponent: EmailComponent = {
          id: Math.random().toString(36).substr(2, 9),
          type: item.type,
          props: getDefaultProps(item.type),
          children: [],
          style: {},
        };
        addComponent(newComponent);
      }
    },
    collect: (monitor) => ({
      isOver: monitor.isOver({ shallow: true }),
    }),
  });

  const getDefaultProps = (type: ComponentType): Record<string, any> => {
    switch (type) {
      case 'header':
        return {
          logo: '',
          title: 'Company Name',
          subtitle: 'Your tagline here',
          backgroundColor: '#ffffff',
          textColor: '#000000',
        };
      case 'text':
        return {
          content: 'Enter your text content here...',
          fontSize: '16px',
          fontWeight: 'normal',
          textAlign: 'left',
          color: '#000000',
          lineHeight: '1.5',
        };
      case 'image':
        return {
          src: 'https://via.placeholder.com/600x300',
          alt: 'Description of the image',
          width: '100%',
          height: 'auto',
          align: 'center',
          borderRadius: '0px',
        };
      case 'button':
        return {
          text: 'Click me',
          url: '#',
          backgroundColor: '#3b82f6',
          textColor: '#ffffff',
          borderRadius: '6px',
          padding: '12px 24px',
          fontSize: '16px',
        };
      case 'divider':
        return {
          color: '#e5e7eb',
          height: '1px',
          margin: '20px 0',
        };
      case 'columns':
        return {
          columns: 2,
          gap: '20px',
          backgroundColor: '#ffffff',
        };
      case 'footer':
        return {
          companyName: 'Company Name',
          address: '123 Main St, City, State 12345',
          phone: '+1 (555) 123-4567',
          email: 'info@company.com',
          socialLinks: [
            { platform: 'Facebook', url: '#' },
            { platform: 'Twitter', url: '#' },
            { platform: 'LinkedIn', url: '#' },
          ],
          unsubscribeText: 'Unsubscribe',
        };
      case 'spacer':
        return {
          height: '20px',
        };
      default:
        return {};
    }
  };

  const handleComponentClick = (component: EmailComponent) => {
    selectComponent(component);
  };

  const handleDragEnd = (result: any) => {
    if (!result.destination) return;

    const sourceIndex = result.source.index;
    const destinationIndex = result.destination.index;

    if (sourceIndex !== destinationIndex) {
      moveComponent(result.draggableId, destinationIndex);
    }
  };

  return (
    <div className="card p-6">
              <div className="flex items-center justify-between mb-6">
          <h2 className="text-xl font-semibold text-gray-900">
            Email Template Builder
          </h2>
          <div className="flex items-center space-x-4">
            <div className="text-sm text-gray-500">
              {template.components.length} components
            </div>
            <button
              onClick={() => setIsPreviewMode(true)}
              className="btn-primary text-sm px-4 py-2"
              disabled={template.components.length === 0}
            >
              <svg className="w-4 h-4 mr-2 inline" fill="none" stroke="currentColor" viewBox="0 0 24 24">
                <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M15 12a3 3 0 11-6 0 3 3 0 016 0z" />
                <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M2.458 12C3.732 7.943 7.523 5 12 5c4.478 0 8.268 2.943 9.542 7-1.274 4.057-5.064 7-9.542 7-4.477 0-8.268-2.943-9.542-7z" />
              </svg>
              Preview
            </button>
          </div>
        </div>

      {/* Canvas */}
      <div
        ref={drop}
        className={`min-h-[600px] border-2 border-dashed rounded-lg transition-colors ${
          isOver 
            ? 'border-primary-400 bg-primary-50' 
            : 'border-gray-300 bg-gray-50'
        }`}
      >
        {template.components.length === 0 ? (
          <div className="flex flex-col items-center justify-center h-full py-20">
            <svg className="w-16 h-16 text-gray-300 mb-4" fill="none" stroke="currentColor" viewBox="0 0 24 24">
              <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M7 21a4 4 0 01-4-4V5a2 2 0 012-2h4a2 2 0 012 2v12a4 4 0 01-4 4zM21 5a2 2 0 00-2-2h-4a2 2 0 00-2 2v12a4 4 0 004 4h4a2 2 0 002-2V5z" />
            </svg>
            <h3 className="text-lg font-medium text-gray-900 mb-2">
              Start building your email
            </h3>
            <p className="text-gray-500 text-center max-w-sm">
              Drag components from the left sidebar to start creating your email template
            </p>
          </div>
        ) : (
          <div className="p-4 space-y-4">
            {template.components.map((component, index) => (
              <DraggableComponent
                key={component.id}
                component={component}
                index={index}
                onClick={() => handleComponentClick(component)}
                isSelected={selectedComponent?.id === component.id}
                onMove={moveComponent}
              />
            ))}
          </div>
        )}
      </div>

      {/* Template Settings */}
      <div className="mt-6 pt-6 border-t border-gray-200">
        <h3 className="text-sm font-medium text-gray-700 mb-3">Template Settings</h3>
        <div className="grid grid-cols-3 gap-4">
          <div>
            <label className="block text-xs font-medium text-gray-500 mb-1">
              Width
            </label>
            <input
              type="text"
              value={template.settings.width}
              className="input-field text-sm"
              placeholder="600px"
            />
          </div>
          <div>
            <label className="block text-xs font-medium text-gray-500 mb-1">
              Background
            </label>
            <input
              type="color"
              value={template.settings.backgroundColor}
              className="w-full h-8 rounded border border-gray-300"
            />
          </div>
          <div>
            <label className="block text-xs font-medium text-gray-500 mb-1">
              Font Family
            </label>
            <select className="input-field text-sm">
              <option value="Arial, sans-serif">Arial</option>
              <option value="Helvetica, sans-serif">Helvetica</option>
              <option value="Georgia, serif">Georgia</option>
              <option value="Times New Roman, serif">Times New Roman</option>
            </select>
          </div>
        </div>
      </div>

      {/* Preview Component */}
      <Preview
        template={template}
        isPreviewMode={isPreviewMode}
        onTogglePreview={() => setIsPreviewMode(false)}
      />
    </div>
  );
};
