import React from 'react';
import { useDrag, useDrop } from 'react-dnd';
import { EmailComponent } from '../../types';
import { ComponentRenderer } from './ComponentRenderer';

interface DraggableComponentProps {
  component: EmailComponent;
  index: number;
  onClick: () => void;
  isSelected: boolean;
  onMove: (id: string, newIndex: number) => void;
  onDelete: (id: string) => void;
}

export const DraggableComponent: React.FC<DraggableComponentProps> = ({
  component,
  index,
  onClick,
  isSelected,
  onMove,
  onDelete,
}) => {
  const [{ isDragging }, drag] = useDrag({
    type: 'EXISTING_COMPONENT',
    item: { id: component.id, index },
    collect: (monitor) => ({
      isDragging: monitor.isDragging(),
    }),
  });

  const [{ isOver }, drop] = useDrop({
    accept: 'EXISTING_COMPONENT',
    hover: (item: { id: string; index: number }, monitor) => {
      if (!monitor.isOver()) {
        return;
      }

      if (item.index === index) {
        return;
      }

      onMove(item.id, index);
      item.index = index;
    },
    collect: (monitor) => ({
      isOver: monitor.isOver(),
    }),
  });

  return (
    <div
      ref={(node) => drag(drop(node))}
      className={`relative group transition-all duration-200 ${
        isDragging ? 'opacity-50 scale-95' : ''
      } ${isOver ? 'border-l-4 border-primary-500' : ''}`}
    >
      <ComponentRenderer
        component={component}
        onClick={onClick}
        isSelected={isSelected}
      />

      {/* Drag Handle */}
      <div className="absolute top-2 left-2 opacity-0 group-hover:opacity-100 transition-opacity">
        <div className="p-1 bg-white rounded shadow-sm border border-gray-200 cursor-move">
          <svg className="w-3 h-3 text-gray-600" fill="none" stroke="currentColor" viewBox="0 0 24 24">
            <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M4 8h16M4 16h16" />
          </svg>
        </div>
      </div>

      {/* Component Type Badge */}
      <div className="absolute top-2 left-12">
        <span className="inline-flex items-center px-2 py-1 rounded-full text-xs font-medium bg-gray-100 text-gray-800">
          {component.type}
        </span>
      </div>
    </div>
  );
};
