import React from 'react';
import { useDrop } from 'react-dnd';
import { ComponentType } from '../../types';

interface DropZoneProps {
  index: number;
  onDrop: (type: ComponentType, index: number) => void;
}

export const DropZone: React.FC<DropZoneProps> = ({ index, onDrop }) => {
  const [{ isOver }, drop] = useDrop({
    accept: 'COMPONENT',
    drop: (item: { type: ComponentType }, monitor) => {
      if (!monitor.didDrop()) {
        onDrop(item.type, index);
      }
    },
    collect: (monitor) => ({
      isOver: monitor.isOver({ shallow: true }),
    }),
  });

  return (
    <div
      ref={drop}
      className={`h-8 transition-all duration-200 ${
        isOver 
          ? 'bg-blue-100 border-2 border-dashed border-blue-400' 
          : 'bg-transparent border-2 border-dashed border-transparent hover:border-gray-300 hover:bg-gray-50'
      }`}
    >
      {isOver && (
        <div className="flex items-center justify-center h-full">
          <div className="bg-blue-500 text-white px-3 py-1 rounded-full text-sm font-medium shadow-lg">
            Drop here to insert
          </div>
        </div>
      )}
    </div>
  );
};
