import React from 'react';
import { ComponentType } from '../../types';
import { ComponentItem } from './ComponentItem';

interface ComponentsSectionProps {
  isExpanded: boolean;
  onToggle: () => void;
  onDragStart: (e: React.DragEvent, type: ComponentType) => void;
}

export const ComponentsSection: React.FC<ComponentsSectionProps> = ({
  isExpanded,
  onToggle,
  onDragStart
}) => {
  const components = [
    { type: 'header' as ComponentType, label: 'Header', icon: 'üè∑Ô∏è' },
    { type: 'text' as ComponentType, label: 'Text', icon: 'üìù' },
    { type: 'image' as ComponentType, label: 'Image', icon: 'üñºÔ∏è' },
    { type: 'button' as ComponentType, label: 'Button', icon: 'üîò' },
    { type: 'divider' as ComponentType, label: 'Divider', icon: '‚ûñ' },
    { type: 'spacer' as ComponentType, label: 'Spacer', icon: '‚ÜïÔ∏è' },
    { type: 'footer' as ComponentType, label: 'Footer', icon: 'üèÅ' }
  ];

  return (
    <div className="border border-gray-200 rounded-lg">
      <button
        onClick={onToggle}
        className="flex items-center justify-between w-full p-3 text-left hover:bg-gray-50 transition-colors"
      >
        <h3 className="text-sm font-medium text-gray-700">Components</h3>
        <svg
          className={`w-4 h-4 text-gray-500 transition-transform ${isExpanded ? 'rotate-180' : ''}`}
          fill="none"
          stroke="currentColor"
          viewBox="0 0 24 24"
        >
          <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M19 9l-7 7-7-7" />
        </svg>
      </button>

      {isExpanded && (
        <div className="p-3 border-t border-gray-200 space-y-2">
          {components.map((component) => (
            <ComponentItem
              key={component.type}
              type={component.type}
              label={component.label}
              icon={component.icon}
              onDragStart={onDragStart}
            />
          ))}
        </div>
      )}
    </div>
  );
};
