import React from 'react';
import { ComponentType } from '../../types';

interface ComponentItemProps {
  type: ComponentType;
  label: string;
  icon?: React.ReactNode;
  onDragStart: (e: React.DragEvent, type: ComponentType) => void;
}

export const ComponentItem: React.FC<ComponentItemProps> = ({
  type,
  label,
  icon,
  onDragStart
}) => {
  return (
    <div
      draggable
      onDragStart={(e) => onDragStart(e, type)}
      className="flex items-center p-3 bg-white border border-gray-200 rounded-lg cursor-move hover:bg-gray-50 hover:border-gray-300 transition-colors"
    >
      {icon && <span className="mr-2 text-gray-500">{icon}</span>}
      <span className="text-sm font-medium text-gray-700">{label}</span>
    </div>
  );
};
