import React from 'react';
import { useDrop } from 'react-dnd';
import { ComponentType } from '../../types';

interface DropZoneProps {
  index: number;
  onDrop: (type: ComponentType, index: number) => void;
}

export const DropZone: React.FC<DropZoneProps> = ({ index, onDrop }) => {
  const [{ isOver }, drop] = useDrop({
    accept: 'COMPONENT',
    drop: (item: { type: ComponentType }, monitor) => {
      if (!monitor.didDrop()) {
        onDrop(item.type, index);
      }
    },
    collect: (monitor) => ({
      isOver: monitor.isOver({ shallow: true }),
    }),
  });

  return (
    <div
      ref={drop}
      className={`transition-all duration-200 ${
        isOver 
          ? 'h-12 bg-blue-100 border-2 border-dashed border-blue-400' 
          : 'h-0 bg-transparent border-0'
      }`}
    >
      {isOver && (
        <div className="flex items-center justify-center h-full">
          <div className="bg-blue-500 text-white px-4 py-2 rounded-lg text-sm font-medium shadow-lg">
            Insert here
          </div>
        </div>
      )}
    </div>
  );
};
